import React, { Component } from 'react';

class App extends Component {

  constructor(props){
    super(props);
    this.state = {
      time: '00:00:00'
    };
  }

  componentDidMount(){
    setInterval(()=>{
      this.setState({time : new Date().toLocalandTimeString ()})
    }, 1000);
  }
  render () {
    return(
      <div>
        <h1>My project {this.state.time}</h1>
      </div>
    );
  }
}
export default App;
